<table class="display" id="<?php echo $id; ?>">	<thead>		<tr>		<?php 			foreach($header as $headerRow){				//print header atas				echo '<th>'.$headerRow.'</th>';			}		?>		</tr>	</thead>	<tbody>		<?php			$modal_id = $column_id = 0;			//looping untuk baris table						foreach($content as $row){							// modal header dan content akan otomatis reset setiap barisnya				$modal_header = $modal_content = '';				echo '<tr>';								//looping kolom table				foreach($row as $contentRow){					if($column_id == '0')					{						$modal_header = substr(strip_tags($contentRow), 0 ,50);						echo '<td><a href="" data-toggle="modal" data-target="#content'.$modal_id.'">'.$modal_header.'</a></td>';						$column_id++;					}else{						if($column_id == '1')							$modal_content = $contentRow;						echo '<td>'.substr(strip_tags($contentRow), 0 ,50).'</td>';						$column_id++;					}				}				echo '</tr>';								//modal body generate				echo '				<div class="modal hide  fade" id="content'.$modal_id.'">					<div class="modal-header">						<button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>						<h3 id="myModalLabel">'.$modal_header.'</h3>					</div>					<div class="modal-body">						'.$modal_content.'					</div>				</div>';								//pas pindah baris table $column_id di reset, tujuannya untuk kalkulasi ulang tiap kolom				$column_id = 0;								//setiap pindah baris $modal_id yang dipakai untuk generate ID untuk modal berubah.				// agar tiap modal window yang digenerate memiliki ID yang berbeda				$modal_id++;			}		?>	</tbody>	<tfoot>		<tr>		<?php 			foreach($header as $headerRow){				//print header bawah				echo '<th>'.$headerRow.'</th>';			}		?>		</tr>	</tfoot></table>