<table class="display" id="<?php echo $id; ?>">	<thead>		<tr>		<?php 			foreach($header as $headerRow){				//print header atas				echo '<th>'.$headerRow.'</th>';			}		?>		</tr>	</thead>	<tbody>		<?php			//looping kolom table			if($content) 			foreach($content as $row){				echo '<tr>';				echo form_open('ajax_service/user_delete', array('class' => 'ajaxTableDelete'));				echo '<td>'.$row['SAS_USER_NAME'].'</td>';				echo '<td>'.$row['SAS_USERNAME'].'</td>';				echo '<td class="center">'.$row['SAS_LAST_UPDATE'].'</td>';				echo '<td class="center">'.$row['SAS_LAST_UPDATE_BY'].'</td>';				echo '<td class="center"><i class="icon-file"></i>'.anchor($this->uri->segment(1).'/user/'.$row['SAS_USER_ID'], 'Tampilkan', 'class="btn btn-link"').'</td>';				echo '<td class="center"><i class="icon-pencil"></i>'.anchor($this->uri->segment(1).'/user/'.$row['SAS_USER_ID'], 'Ubah', 'class="btn btn-link"').'</td>';				echo '<td class="center"><i class="icon-trash"></i><input type="submit" class="btn btn-link" value="Hapus" /></td>';				echo form_hidden(array('id' => $row['SAS_USER_ID']));				echo form_close();				echo '</tr>';			}		?>	</tbody>	<tfoot>		<tr>		<?php 			foreach($header as $headerRow){				//print header bawah				echo '<th>'.$headerRow.'</th>';			}		?>		</tr>	</tfoot></table>