<table class="display" id="<?php echo $id; ?>">	<thead>		<tr>		<?php 			foreach($header as $headerRow){				//print header atas				echo '<th>'.$headerRow.'</th>';			}		?>		</tr>	</thead>	<tbody>		<?php			$modalHeaderID = 0;			foreach($content as $row){							$label = explode(",",$row['SAS_BERITA_TAG']);				$tag_berita = '';				foreach($label as $row_label) 					$tag_berita = $tag_berita.'<span class="label label-info">'.$row_label.'</span>';									echo '<tr>';				echo '<td>'.$row['SAS_BERITA_TITLE'].'</td>';				echo '<td class="center"><a href="" data-target="#modal'.$modalHeaderID.'" id="modalButton'.$modalHeaderID.'" data-toggle="modal" "><i class="icon-file"></i>Tampilkan<a></td>';				echo '<td class="center">'.$row['SAS_ADDED_BY'].'</td>';				echo '<td class="center">'.$row['SAS_ADDED_DATE'].'</td>';				echo '<td class="center">'.$tag_berita.'</td>';				echo '</tr>';				$modalHeaderID++;			}		?>	</tbody>	<tfoot>		<tr>		<?php 			foreach($header as $headerRow){				//print header bawah				echo '<th>'.$headerRow.'</th>';			}		?>		</tr>	</tfoot></table><?php	$modalBodyID = 0;	foreach($content as $row){		//modal body generate		echo '		<div class="modal hide fade" id="modal'.$modalBodyID.'">			<div class="modal-header">				<button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>				<h3 id="myModalLabel">'.$row['SAS_BERITA_TITLE'].'</h3>			</div>			<div class="modal-body">'.$row['SAS_BERITA_CONTENT'].'</div>		</div>';		$modalBodyID++;	}?>